import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';

const ProductCard = ({ product }) => {
  const [selectedSize, setSelectedSize] = useState(null);
  const [selectedColor, setSelectedColor] = useState(null);
  const { addToCart } = useCart();

  const handleAddToCart = () => {
    if (!selectedSize || !selectedColor) return;
    
    addToCart(product, selectedSize, selectedColor);
    alert(`${product.name} добавлен в корзину!`);
  };

  return (
    <div className="product-card">
      <div className="card-badge">Новинка</div>
      <Link to={`/products/${product.id}`} className="product-link">
        <div className="product-image-container">
          <img
            src={product.image}
            alt={product.name}
            className="product-image"
            onError={(e) => {
              e.target.onerror = null;
              e.target.src = '/images/placeholder.jpg';
            }}
          />
          <button className="quick-view-btn">Быстрый просмотр</button>
        </div>
      </Link>

      <div className="product-info">
        <h3 className="product-name">{product.name}</h3>
        <div className="price-section">
          <span className="current-price">{product.price.toLocaleString()} ₽</span>
          {product.oldPrice && (
            <span className="old-price">{product.oldPrice.toLocaleString()} ₽</span>
          )}
        </div>
        <div className="color-options">
          {product.colors?.map((color, index) => (
            <span 
              key={index}
              className={`color-dot ${selectedColor === color ? 'selected' : ''}`}
              style={{ backgroundColor: color }}
              title={color}
              onClick={() => setSelectedColor(color)}
            />
          ))}
        </div>
        <div className="size-options">
          {product.sizes?.map((size, index) => (
            <button
              key={index}
              className={`size-btn ${selectedSize === size ? 'selected' : ''}`}
              onClick={() => setSelectedSize(size)}
            >
              {size}
            </button>
          ))}
        </div>
      </div>

      <div className="card-actions">
        <button 
          className="add-to-cart-btn"
          onClick={handleAddToCart}
          disabled={!selectedSize || !selectedColor}
        >
          <svg width="20" height="20" viewBox="0 0 24 24">
            <path fill="currentColor" d="M17,18A2,2 0 0,1 19,20A2,2 0 0,1 17,22C15.89,22 15,21.1 15,20C15,18.89 15.89,18 17,18M1,2H4.27L5.21,4H20A1,1 0 0,1 21,5C21,5.17 20.95,5.34 20.88,5.5L17.3,11.97C16.96,12.58 16.3,13 15.55,13H8.1L7.2,14.63L7.17,14.75A0.25,0.25 0 0,0 7.42,15H19V17H7A2,2 0 0,1 5,15C5,14.18 5.59,13.5 6.36,13.31L8.1,10L5.4,5H1V2M7,18A2,2 0 0,1 9,20A2,2 0 0,1 7,22C5.89,22 5,21.1 5,20C5,18.89 5.89,18 7,18Z"/>
          </svg>
          В корзину
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
