import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const ProductDetail = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedSize, setSelectedSize] = useState(null);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    // Запрос к API для получения данных о конкретном товаре
    const mockProducts = [
      { 
        id: 1, 
        name: 'Nike Air Max 95', 
        price: 12000, 
        brand: 'Nike', 
        image: '../images/nike-air-max.jpg',
        description: 'Легендарные кроссовки с технологией Air для максимального комфорта.',
        sizes: [39, 40, 41, 42, 43, 44],
        colors: ['black', 'white', 'red']
      },
    ];
    
    setTimeout(() => {
      const foundProduct = mockProducts.find(p => p.id === parseInt(id));
      setProduct(foundProduct);
      setLoading(false);
    }, 500);
  }, [id]);

  if (loading) return <p>Загрузка...</p>;
  if (!product) return <p>Товар не найден</p>;

  return (
    <div className="product-detail">
      <div className="product-images">
        <img src={product.image} alt={product.name} className="main-image" />
        <div className="thumbnail-container">
          {/* Здесь можно добавить миниатюры других изображений */}
        </div>
      </div>
      
      <div className="product-info">
        <h1>{product.name}</h1>
        <p className="brand">{product.brand}</p>
        <p className="price">{product.price} ₽</p>
        <p className="description">{product.description}</p>
        
        <div className="size-selector">
          <h3>Размер:</h3>
          <div className="sizes">
            {product.sizes.map(size => (
              <button 
                key={size} 
                className={selectedSize === size ? 'active' : ''}
                onClick={() => setSelectedSize(size)}
              >
                {size}
              </button>
            ))}
          </div>
        </div>
        
        <div className="quantity-selector">
          <h3>Количество:</h3>
          <button onClick={() => setQuantity(Math.max(1, quantity - 1))}>-</button>
          <span>{quantity}</span>
          <button onClick={() => setQuantity(quantity + 1)}>+</button>
        </div>
        
        <button 
          className="add-to-cart" 
          disabled={!selectedSize}
        >
          Добавить в корзину
        </button>
      </div>
    </div>
  );
};

export default ProductDetail;
